mapper.select:
SELECT IFNULL(SUM( pap.`value` ),0) AS papCount, cm.corporation_id AS id FROM kassie_calendar_paps pap JOIN corporation_members cm ON pap.character_id = cm.character_id WHERE cm.corporation_id in
#{item.id}
AND pap.YEAR = #{year} AND pap.`month` = #{month} GROUP BY cm.corporation_id

SELECT IFNULL(SUM( pap.`value` ),0) AS paps, u.name, cm.corporation_id AS id FROM kassie_calendar_paps pap JOIN corporation_members cm ON pap.character_id = cm.character_id join refresh_tokens rt on pap.character_id = rt.character_id join users u on rt.user_id = u.id WHERE cm.corporation_id in
#{item.id}
AND pap.YEAR = #{year} AND pap.`month` = #{month} GROUP BY rt.user_id order by paps desc
